import React, { Component } from "react";
import UploadService from "../../services/upload-files.service";
import FileViewer from 'react-file-viewer';
import { Button,FileUploader } from 'carbon-components-react';
import ResultsList from './ResultsList'
import { WarningFilled16 } from '@carbon/icons-react';

export default class UploadFiles extends Component {
  constructor(props) {
    super(props);
    this.selectFile = this.selectFile.bind(this);
    this.upload = this.upload.bind(this);

    this.state = {
      selectedFiles: undefined,
      currentFile: undefined,
      progress: 0,
      message: "Please Upload Your Document",
      results: [],
      docUrls: [ { fileData: '' } ],
      fileInfos: [],
      docUrl: undefined
    };
  }

  selectFile(event) {
    console.log(event.target.files)
    this.setState({
      selectedFiles: event.target.files,
    });
  }

  upload() {
    let currentFile = this.state.selectedFiles[0];

    this.setState({
      progress: 0,
      currentFile: currentFile,
    });

    UploadService.upload(currentFile, (event) => {
      this.setState({
        progress: Math.round((100 * event.loaded) / event.total),
      });
    }).then((response) => {
        console.log(response.data.transactionalId);
        var getImagePath = URL.createObjectURL(currentFile);
        console.log(getImagePath);
        this.setState({
            message: "Uploaded Document, Now Processing....."

        })
        UploadService.executeRules(response.data.transactionalId,(event) =>{
            this.setState({
            progress: Math.round((100 * event.loaded) / event.total),
          });
        }).then((response)=>{
          console.log(response.data)

          this.setState({
            results: response.data.values,
            message: "Document Processed",
            transactionId: response.data.transactionalId,
            docUrl: "http://localhost:3001/api/documentunderstanding/getdoc/" + response.data.transactionalId+ "/pdf"
          });
          console.log(this.state);
          return "stuff"
        })
      })
      .catch(() => {
        this.setState({
          progress: 0,
          message: "Could not upload the file!",
          currentFile: undefined,
        });
      });

    this.setState({
      selectedFiles: undefined,
    });
  }



  render() {
    const {
      selectedFiles,
      currentFile,
      progress,
      message,
      fileInfos,
    } = this.state;

    return (
  <div>
    <h1>{this.state.message}</h1>
      <div>
        <FileUploader
          accept={[
            '.jpg',
            '.pdf'
          ]}
          onChange={this.selectFile}
          buttonKind="primary"
          buttonLabel="Add files"
          filenameStatus="edit"
          iconDescription="Clear file"
          labelDescription="only .pdf and jpg files at 500mb or less"
          labelTitle="Upload"
        >  <WarningFilled16 aria-label="Add" className="my-custom-class" /></FileUploader>
        <Button disabled={!selectedFiles} onClick={this.upload}>
          Process Document

        </Button>
      </div>
      {this.state.message=="Document Processed" &&
      <div>
        <ResultsList results={this.state.results} />
       </div>
     }
       {this.state.docUrl != undefined  &&
         <div>

        <FileViewer fileType="pdf" filePath={this.state.docUrl} />
        </div>
       }




      </div>
    );
  }
}
