import React, { Component } from "react";
import UploadService from "../services/upload-files.service";
import DocViewer from "react-doc-viewer";

export default class UploadFiles extends Component {
  constructor(props) {
    super(props);
    this.selectFile = this.selectFile.bind(this);
    this.upload = this.upload.bind(this);

    this.state = {
      selectedFiles: undefined,
      currentFile: undefined,
      progress: 0,
      message: [],
      docUrls: [ { uri: '' } ],
      fileInfos: [],
    };
  }

  selectFile(event) {
    this.setState({
      selectedFiles: event.target.files,
    });
  }

  upload() {
    let currentFile = this.state.selectedFiles[0];

    this.setState({
      progress: 0,
      currentFile: currentFile,
    });

    UploadService.upload(currentFile, (event) => {
      this.setState({
        progress: Math.round((100 * event.loaded) / event.total),
      });
    })
      .then((response) => {
        console.log(response.data);
        this.setState({
          docUrls: [{uri: "/api/documentunderstanding/captureDocument/getdoc/" + response.data.transactionalId+ "/pdf"}],
          message: response.data.values,
          transactionId: response.data.transactionalId
        });
        return "stuff"
      })
      .then((files) => {
        this.setState({
          fileInfos: files.data,
        });
      })
      .catch(() => {
        this.setState({
          progress: 0,
          message: "Could not upload the file!",
          currentFile: undefined,
        });
      });

    this.setState({
      selectedFiles: undefined,
    });
  }

  render() {
    const {
      selectedFiles,
      currentFile,
      progress,
      message,
      fileInfos,
    } = this.state;

    return (
      <div>

        <label className="btn btn-default">
          <input type="file" onChange={this.selectFile} />
        </label>

        <button
          className="btn btn-success"
          disabled={!selectedFiles}
          onClick={this.upload}
        >
          Upload
        </button>


        <ul>
          {this.state.message.map(d => (<li key={d.fieldName}>{d.fieldName} : {d.fieldValue}</li>))}
          </ul>
          <DocViewer documents={this.state.docUrls} />


      </div>
    );
  }
}
